// 

import React, { useState } from 'react';
import './ProductList.css';
import ProductAdd from './ProductAdd';

interface Product {
  id: number;
  name: string;
  price: number;
  description?: string;
}

interface ProductListProps {
  products: Product[];
  setProducts: React.Dispatch<React.SetStateAction<Product[]>>;
}

const ProductList: React.FC<ProductListProps> = ({ products, setProducts }) => {
  const [showPopup, setShowPopup] = useState(false);

  const togglePopup = () => {
    setShowPopup(!showPopup);
  };

  return (
    <div className="product-list-container">
      <div className="header">
        <h1>Product List</h1>
        <button className="add-product-button" onClick={togglePopup}>
          Add Product
        </button>
      </div>
      <ul>
        <li className="product-header">
          <span>Product ID</span>
          <span>Name</span>
          <span>Price</span>
          <span>Description</span>
        </li>
        {products.map(product => (
          <li key={product.id} className="product-item">
            <span>{product.id}</span>
            <span>{product.name}</span>
            <span>${product.price}</span>
            <span>{product.description}</span>
          </li>
        ))}
      </ul>
      {showPopup && <ProductAdd products={products} setProducts={setProducts} togglePopup={togglePopup} />}
    </div>
  );
};

export default ProductList;
